    [AttributeUsage(AttributeTargets.Method)]    public class MudRequestAttribute : attribute    {        /// <summary>        /// This attribute can be used on a static method to make the multi-user domain server software see this method as a MUD request handler.        /// </summary>        /// <param name="rName">The header ID of the request this method should handle.</param>        public MudRequestAttribute(string rName, Type expected)        {            RequestName = rName;            ExpectedType = expected;        }        public string RequestName { get; private set; }        public Type ExpectedType { get; private set; }    }    [Obsolete("This feature will be removed in Milestone 2.")]
    [AttributeUsage(AttributeTargets.Method)]    public class MudResponseAttribute : attribute    {        /// <summary>        /// Clients will look for static methods marked with this attribute and run them first. If no attribute is found with the given header ID, the client may invoke a delegate with the message information.        /// </summary>        /// <param name="rName">The header ID of the response that this method will handle.</param>        public MudResponseAttribute(string rName)        {            ResponseName = rName;        }        public string ResponseName { get; private set; }    }    [Obsolete("This feature will be removed in Milestone 2.")]
    public class ChatLogRequest    {        public ChatLogRequest(string chan, int backtrack = 0)        {            Channel = chan;            Backtrack = backtrack;        }        public int Backtrack { get; set; }        public string Channel { get; set; }    }}